import React, { useEffect, useState } from 'react';
import axios, { AxiosResponse } from 'axios';
import { Button, Grid } from '@material-ui/core';
import styled from 'styled-components';

const StyledContainer = styled.div`
    padding:100px;
`;

const StyledGrid = styled(Grid)`
    padding: 2px;
    background: #eee;
    border-bottom: 4px solid black;
`;

const StyledBold = styled.span`
    font-weight:bold;
`;

const Users = () => {
    const [data, setData] = useState({} as AxiosResponse<any>);

    useEffect(() => {
        axios.get("http://127.0.0.1:5000/data").then((data) => {
            setData(data);
        });
    }, []);

    const handleDeleteClick = (id: string) => {
        axios.delete("http://127.0.0.1:5000/data/" + id).then((data) => {
            axios.get("http://127.0.0.1:5000/data").then((data) => {
                setData(data);
            });
        });
    }

    const renderData = () => {
        return (
            <React.Fragment>
                <StyledGrid container spacing={3} alignItems="center" direction="row" justify={"center"}>
                    <React.Fragment><Grid item xs={3}><StyledBold>NAME</StyledBold></Grid> <Grid item xs={3}><StyledBold>AGE</StyledBold></Grid><Grid item xs={3}><StyledBold>ID</StyledBold></Grid><Grid item xs={3}></Grid></React.Fragment>
                </StyledGrid>
                {
                    (data && data.data) ?
                        data.data.map((each: any, i: number) =>
                            <StyledGrid container alignItems="center" direction="row" spacing={3} justify={"center"}>
                                <Grid item xs={3}>{each.name}</Grid> <Grid item xs={3}>{each.age}</Grid> <Grid item xs={3}>{each.id}</Grid><Grid item xs={3}><Button onClick={() => handleDeleteClick(each.id)}>delete</Button></Grid>
                            </StyledGrid>
                        ) : <span></span>
                }
            </React.Fragment >
        )
    }


    return (
        <StyledContainer>
            {renderData()}
        </StyledContainer>
    );
}

export default Users;
