import React, { useEffect, useState } from 'react';
import axios, { AxiosResponse } from 'axios';
import { Button, Grid } from '@material-ui/core';
import styled from 'styled-components';

const StyledContainer = styled.div`
    padding:100px;
`;

const StyledTable = styled.table`
    tr {
        border-bottom: 10px solid black;
    }
`;


const Users = () => {
    const [data, setData] = useState({} as AxiosResponse<any>);

    useEffect(() => {
        axios.get("http://127.0.0.1:5000/data").then((data) => {
            setData(data);
        });
    }, []);

    const handleDeleteClick = (id: string) => {
        axios.delete("http://127.0.0.1:5000/data/" + id).then((data) => {
            axios.get("http://127.0.0.1:5000/data").then((data) => {
                setData(data);
            });
        });
    }

    const renderData = () => {
        return (
            <StyledTable>
                <tr><td colSpan={2}>NAME</td> <td colSpan={1}>AGE</td><td colSpan={5}>ID</td><td colSpan={4}></td></tr>
                {
                    (data && data.data) ?
                        data.data.map((each: any, i: number) =>
                            <tr><td colSpan={2}>{each.name}</td> <td colSpan={1}>{each.age}</td> <td colSpan={5}>{each.id}</td><td colSpan={4}><Button onClick={() => handleDeleteClick(each.id)}>delete</Button></td></tr>
                        ) : <span></span>
                }
            </StyledTable >
        )
    }


    return (
        <StyledContainer>
            {renderData()}
        </StyledContainer>
    );
}

export default Users;
