import React, { useEffect, useState } from 'react';
import axios, { AxiosResponse } from 'axios';
import { Button, Grid, Box } from '@material-ui/core';
import styled from 'styled-components';

const StyledContainer = styled.div`
    padding:100px;
`;

const StyledSpan = styled.span`
    border-bottom: 1px solid #aaa;
`;

const StyledGrid = styled(Grid)`
    padding: 2px;
    background: #eee;
`;

const Users = () => {
    const [data, setData] = useState({} as AxiosResponse<any>);

    useEffect(() => {
        axios.get("http://127.0.0.1:5000/data").then((data) => {
            setData(data);
        });
    }, []);

    const handleDeleteClick = (id: string) => {
        axios.delete("http://127.0.0.1:5000/data/" + id).then((data) => {
            axios.get("http://127.0.0.1:5000/data").then((data) => {
                setData(data);
            });
        });
    }

    const renderData = () => {
        return (
            <StyledGrid container alignItems="center" direction="row" spacing={1} justify={"center"}>
                {(data && data.data) ?
                    (data.data.map((each: any, i: number) =>
                        <React.Fragment key={i}><Grid item xs={3}>{each.name}</Grid> <Grid item xs={3}>{each.age}</Grid> <Grid item xs={3}>{each.id}</Grid><Grid item xs={3}><Button onClick={() => handleDeleteClick(each.id)}>delete</Button></Grid></React.Fragment>)
                    ) : <span></span>
                }
            </StyledGrid>)
    }


    return (
        <StyledContainer>
            {renderData()}
        </StyledContainer>
    );
}

export default Users;
