import React, { useState } from 'react';
import { Button, Grid, TextField } from '@material-ui/core';
import axios from 'axios';
import styled from 'styled-components';

interface ICreateUserProps {
    changeTab: (active: any) => void;
}

const StyledInformation = styled.div`
    padding: 20px;
    border: 1px solid #aaa;
    text-align: left;
    background-color:#eee;
`;

const StyledContainer = styled.div`
    padding:30px;
`;

const StyledWarning = styled.div`
    padding:20px;
    border: 1px solid #aaa;
    background-color: orange;
`;

const StyledWarningEmail = styled.div`
    padding:20px;
    border: 1px solid #aaa;
    background-color: red;
`;

const SignUp = (props: ICreateUserProps) => {
    const [firstname, setFirstname] = useState('');
    const [lastname, setLastname] = useState('');
    const [email, setEmail] = useState('');
    const [address, setAddress] = useState('');
    const [signuptried, setSignuptried] = useState(false);
    const [fieldsWarning, setFieldsWarning] = useState('');
    const [emailWarning, setEmailWarning] = useState('');

    const validateFields = () => {
        let isValid = true;
        [firstname, lastname, email, address].forEach(
            (each) => {
                if (!each || each === '') {
                    isValid = false;
                    setFieldsWarning('Please make sure you filled all fields');
                }
            }
        );

        if (!isValid) return isValid;

        if (!/^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/.test(email)) {
            isValid = false;
            setEmailWarning('Please enter a valid email address');
            return isValid;
        }

        return isValid;
    }

    const handleClick = () => {
        setSignuptried(true);
        if (validateFields()) {
            const headers = {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*'
            };
            axios.post("http://127.0.0.1:5000/broker", JSON.stringify({
                variables: {
                    agency_id: 1,
                    firstname: firstname,
                    lastname: lastname,
                    email: email,
                    broker_address: address
                }
            }), { headers }).then((response) => {
                if (!response.data.error)
                    props.changeTab(1);
                else
                    setFieldsWarning(response.data.error);
            });
        }
    }

    const handleChangeFirstname = (e: any) => {
        signuptried && validateFields();
        setFirstname(e.target.value);
    }

    const handleChangeLastname = (e: any) => {
        signuptried && validateFields();
        setLastname(e.target.value);
    }

    const handleChangeEmail = (e: any) => {
        signuptried && validateFields();
        setEmail(e.target.value);
    }

    const handleChangeAddress = (e: any) => {
        signuptried && validateFields();
        setAddress(e.target.value);
    }


    return (
        <div>
            <React.Fragment>
                <StyledContainer>
                    <Grid container spacing={3}>
                        <Grid item md={12}>
                            {(fieldsWarning && fieldsWarning !== '') ? <StyledWarning>{fieldsWarning}</StyledWarning> :
                                (<StyledInformation>Please fill the form below to sign up</StyledInformation>)}
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField onChange={handleChangeFirstname} required id="firstname" label="Firstname" fullWidth />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField onChange={handleChangeLastname} required id="lastname" label="Lastname" fullWidth />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                onChange={handleChangeEmail}
                                required
                                id="email"
                                label="Email"
                                fullWidth
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                onChange={handleChangeAddress}
                                required
                                id="address"
                                label="Address"
                                helperText="Enter your address on one line"
                                fullWidth
                            />
                        </Grid>
                        <Grid container item xs={12} justify="flex-end">
                            <Button variant="contained" color="primary" onClick={handleClick}>
                                Sign Up
                            </Button>
                        </Grid>
                    </Grid>
                </StyledContainer>
            </React.Fragment>
        </div>
    );
}

export default SignUp;
