import React, { useEffect, useState } from 'react';
import axios, { AxiosResponse } from 'axios';
import { Button, Grid } from '@material-ui/core';
import styled from 'styled-components';

const StyledContainer = styled.div`
    padding:100px;
`;

const StyledGrid = styled(Grid)`
    padding: 2px;
    background: #eee;
    border-bottom: 4px solid black;
`;

const StyledBold = styled.span`
    font-weight:bold;
`;

const StyledButton = styled(Button)`
    &&&{
        background-color: white;
        }
`;

const StyledInformation = styled.div`
    padding: 20px;
    border: 1px solid #eee;
`;

const Brokers = () => {
    const [brokerData, setBrokerData] = useState({} as AxiosResponse<any>);

    useEffect(() => {
        axios.get("http://127.0.0.1:5000/broker").then((data) => {
            setBrokerData(data);
        });
    }, []);

    const handleDeleteClick = (id: string) => {
        axios.delete("http://127.0.0.1:5000/broker/" + id).then((data) => {
            axios.get("http://127.0.0.1:5000/broker").then((data) => {
                setBrokerData(data);
            });
        });
    }

    const renderData = () => {
        return (
            <React.Fragment>
                <StyledInformation></StyledInformation>
                <StyledGrid container spacing={3} alignItems="center" direction="row" justify={"center"}>
                    <React.Fragment><Grid item xs={3}><StyledBold>Firstname</StyledBold></Grid> <Grid item xs={3}><StyledBold>Lastname</StyledBold></Grid><Grid item xs={3}><StyledBold>Email</StyledBold></Grid><Grid item xs={3}></Grid></React.Fragment>
                </StyledGrid>
                {
                    (brokerData && brokerData.data) ?
                        brokerData.data.map((each: any, i: number) =>
                            <StyledGrid container alignItems="center" direction="row" spacing={3} justify={"center"}>
                                <Grid item xs={3}>{each.firstname}</Grid> <Grid item xs={3}>{each.lastname}</Grid> <Grid item xs={3}>{each.email}</Grid><Grid item xs={3}><StyledButton onClick={() => handleDeleteClick(each.id)}>delete</StyledButton></Grid>
                            </StyledGrid>
                        ) : <span></span>
                }
            </React.Fragment >
        )
    }


    return (
        <StyledContainer>
            {renderData()}
        </StyledContainer>
    );
}

export default Brokers;
