import React, { useEffect, useState } from 'react';
import axios, { AxiosResponse } from 'axios';
import { Button, Grid, Box } from '@material-ui/core';
import styled from 'styled-components';

const StyledContainer = styled.div`
    padding:100px;
`;

const StyledGrid = styled(Grid)`
    border-bottom: 1px solid #aaa;
    padding: 2px;
    background: #eee;
`;

const Users = () => {
    const [data, setData] = useState({} as AxiosResponse<any>);

    useEffect(() => {
        axios.get("http://127.0.0.1:5000/data").then((data) => {
            setData(data);
        });
    }, []);

    const handleDeleteClick = (id: string) => {
        axios.delete("http://127.0.0.1:5000/data/" + id).then((data) => {
            axios.get("http://127.0.0.1:5000/data").then((data) => {
                setData(data);
            });
        });
    }

    const renderData = () => {
        return (<span>

            {(data && data.data ? data.data.map((each: any) =>
                (
                    <StyledGrid container alignItems="center" direction="row" spacing={1} justify={"center"}>

                        <Grid item xs={3}>{each.name}</Grid> <Grid item xs={3}>{each.age}</Grid> <Grid item xs={3}>{each.id}</Grid><Grid item xs={3}><Button onClick={() => handleDeleteClick(each.id)}>delete</Button></Grid>

                    </StyledGrid>
                )
            ) : '')}
        </span>)
    }


    return (
        <StyledContainer>
            {renderData()}
        </StyledContainer>
    );
}

export default Users;
