import React, { useState } from 'react';
import Typography from '@material-ui/core/Typography';
import { Button, Grid, TextField } from '@material-ui/core';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import axios from 'axios';
import styled from 'styled-components';

const StyledContainer = styled.div`
    padding:100px;
`;

const CreateUser = () => {
    const [name, setName] = useState('');
    const [age, setAge] = useState(0);
    const [ageWarning, setAgeWarning] = useState('');

    const validateFields = () => {
        let isValid = true;
        if (!name || name === '') {
            isValid = false;
        } else if (!age || (typeof age !== 'number')) {
            setAgeWarning('Please enter valid age');
            isValid = false;
        }
        setAgeWarning('');
        return isValid;
    }

    const handleClick = () => {
        if (validateFields()) {
            axios.post("http://127.0.0.1:5000/data", {
                variables: {
                    name: name,
                    age: age
                }
            });
        }
    }

    const handleChangeName = (e: any) => {
        setName(e.target.value);
    }

    const handleChangeAge = (e: any) => {
        if (isNaN(e.target.value)) {
            setAgeWarning('Please enter valid age');
        } else {
            setAge(Number(e.target.value));
            setAgeWarning('');
        }

    }

    return (
        <div>
            <React.Fragment>
                <StyledContainer>
                    <Typography>
                        User Create
                    </Typography>
                    <Grid container spacing={3}>
                        <Grid item xs={12} md={6}>
                            <TextField onChange={handleChangeName} required id="name" label="Name" fullWidth autoComplete="cc-name" />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField onChange={handleChangeAge} required id="age" label="Age" fullWidth autoComplete="cc-name" />
                            {ageWarning && ageWarning !== '' && ageWarning}
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                required
                                id="surname"
                                label="Surname"
                                fullWidth
                                autoComplete="cc-surname"
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField required id="birthdate" label="Date of birth" fullWidth autoComplete="cc-birthdate" />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <TextField
                                required
                                id="profession"
                                label="Profession"
                                helperText="Enter your profession"
                                fullWidth
                                autoComplete="cc-profession"
                            />
                        </Grid>
                        <Grid item xs={12}>

                            <Button variant="contained" color="primary" onClick={handleClick}>
                                save user
                        </Button>
                        </Grid>
                    </Grid>
                </StyledContainer>
            </React.Fragment>
        </div>
    );
}

export default CreateUser;
